#include<bits/stdc++.h>
using namespace std;
// print first n numbers in fibbonacci series
void fib(int n){
    int a=0,b=1,c;
    cout<<a<<" "<<b<<" ";
    for(int i=2;i<n;i++){
        c=a+b;
        cout<<c<<" ";
        a=b;
        b=c;
    }
}
